CREATE TABLE `должности` (
  `id_должности` int(11) NOT NULL AUTO_INCREMENT,
  `наименование` varchar(30) NOT NULL,
  PRIMARY KEY (`id_должности`),
  KEY `id_должности` (`id_должности`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

CREATE TABLE `коддоступа` (
  `id_коддоступа` int(11) NOT NULL AUTO_INCREMENT,
  `коддоступа` varchar(30) NOT NULL,
  PRIMARY KEY (`id_коддоступа`),
  KEY `id_коддоступа` (`id_коддоступа`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

CREATE TABLE `причины` (
  `id_причины` int(11) NOT NULL AUTO_INCREMENT,
  `наименование` varchar(150) NOT NULL,
  PRIMARY KEY (`id_причины`),
  KEY `id_причины` (`id_причины`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

CREATE TABLE `вид_ос` (
  `id_вида_ос` int(11) NOT NULL AUTO_INCREMENT,
  `наименование` varchar(50) NOT NULL,
  PRIMARY KEY (`id_вида_ос`),
  KEY `id_вида_ос` (`id_вида_ос`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

CREATE TABLE `вид_р` (
  `id_вида_р` int(11) NOT NULL AUTO_INCREMENT,
  `наименование` varchar(100) NOT NULL,
  PRIMARY KEY (`id_вида_р`),
  KEY `id_вида_р` (`id_вида_р`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;
